import React, { useState } from 'react'
import { Row, Col, FormControl, Button } from 'react-bootstrap'

const CitySelector = () => {

    const [city, setCity] = useState('')
    const [results, setResults] = useState(null)

    const onSearch = () => {
        fetch(`https://community-open-weather-map.p.rapidapi.com/forecast?q=vancouver&units=metric`, {
            "method": "GET",
            "headers": {
                "x-rapidapi-key": "e67776521cmshd88d4bf5e0c8e46p16cc0fjsn0bd91cdc12f0",
                "x-rapidapi-host": "community-open-weather-map.p.rapidapi.com"
            }
        })
        .then ((response) => response.json())
        .then((results) => setResults(results))
    }

    const onKeyDown = (e) => {
        if(e.keycode === 13) {
            onSearch()
        }
    }

    return (
        <>
            <Row>
                <Col>
                <h1>Search a City</h1>
                </Col>
            </Row>

            <Row>
                <Col xs={4} className="text-center">
                    <FormControl 
                        placeholder="Enter City"
                        onChange={(event) => setCity(event.target.value)}
                        value={city}
                        // allows user to search with enter button. recieves callback function with event inside
                        onKeyDown={onKeyDown}
                    />
                </Col>
            </Row>

            <Row>
                <Col>
                <Button onClick={onSearch} >Check Weather</Button>
                </Col>
            </Row>
        </>
    )
}

export default CitySelector
