import { useState } from 'react'
import Header from './components/Header'
import Navbar from './components/Navbar'
//import Weekdays from './components/Weekdays'
import Forecast from './components/Forecast/Forecast'
import { FaSnowflake, FaCloudRain, FaCloud, FaSun } from 'react-icons/fa'

const App = () => {
const [weekdays, setWeather] = useState(
  [
    {
      id: 1,
      day: 'Sunday',
      weatherType: 'Snow',
      temperature: '-1°',
      picture: <FaSnowflake />,
    },
    {
      id: 2,
      day: 'Monday',
      weatherType: 'Rain',
      temperature: '3°',
      picture: <FaCloudRain />,
    },
    {
      id: 3,
      day: 'Tuesday',
      weatherType: 'Cloudy',
      temperature: '6°',
      picture: <FaCloud />,
    },
    {
      id: 4,
      day: 'Wednesday',
      weatherType: 'Rain',
      temperature: '5°',
      picture: <FaCloudRain />,
    },
    {
      id: 5,
      day: 'Thursday',
      weatherType: 'Cloudy',
      temperature: '7°',
      picture: <FaCloud />,
    },
    {
      id: 6,
      day: 'Friday',
      weatherType: 'Sunny',
      temperature: '12°',
      picture: <FaSun />,
    },
    {
      id: 7,
      day: 'Saturday',
      weatherType: 'Sunny',
      temperature: '11°',
      picture: <FaSun />,
    }
  ]
)

//<Weekdays weekdays={weekdays} /> to go under <Forecast />
  return (
    <div className="container">
      <Navbar />
      <Header />
      <Forecast /> 
    </div>
  )
}

export default App;
