import { useState } from 'react'
import FiveDayConditions from './FiveDayConditions.js'


const FiveDayForecast = () => {

    let [state, setState] = useState({})

    state = {
        fullData: [],
        dailyData: []
      }

    const getFiveDayForecast = () => {
        fetch("https://community-open-weather-map.p.rapidapi.com/forecast?q=san%20francisco%2Cus", {
            "method": "GET",
            "headers": {
                "x-rapidapi-key": "e67776521cmshd88d4bf5e0c8e46p16cc0fjsn0bd91cdc12f0",
                "x-rapidapi-host": "community-open-weather-map.p.rapidapi.com"
            }
        })
        .then(response => response.json())
        .then(data => {
            const dailyData = data.list.filter(reading => reading.dt_txt.includes("18:00:00"))
            this.setState({
              fullData: data.list,
              dailyData: dailyData
            }, () => console.log(this.state))
          })
        .catch(err => {
            console.error(err);
        })
    }
        return (
            <div>
                <h2>5 Day Forecast</h2>
                <button onClick={getFiveDayForecast}>Get 5 Day Forecase</button>
                <FiveDayConditions 
                data={data} />
            </div>
        )

