import React from 'react'
import FiveDayConditions from './FiveDayConditions'
import Forecast from './Forecast'

class FiveDayForecast extends React.Component {
  state = {
    wholeData: [],
    dailyData: []
  }

  componentDidMount = () => {

    const { uriEncodedCity } = require('./Forecast');

    fetch(`https://community-open-weather-map.p.rapidapi.com/forecast?q=${uriEncodedCity}%2Cus&units=metric`, {
        "method": "GET",
        "headers": {
            "x-rapidapi-key": "e67776521cmshd88d4bf5e0c8e46p16cc0fjsn0bd91cdc12f0",
            "x-rapidapi-host": "community-open-weather-map.p.rapidapi.com"
        }
    })
    .then(res => res.json())
    .then(data => {
      const dailyData = data.list.filter(reading => reading.dt_txt.includes("18:00:00"))
      this.setState({
        wholeData: data.list,
        dailyData: dailyData
      }, () => console.log(this.state))
    })
  }


  createFiveDayContainer = () => {
    return this.state.dailyData.map((reading, index) => <FiveDayConditions reading={reading} key={index} />)
  }

  render() {
    return (
      <div>
        {this.createFiveDayContainer()}
      </div>
    )
  }
}

export default FiveDayForecast
